local Players=game:GetService("Players")local LocalPlayer=Players.LocalPlayer local PlayerGui=LocalPlayer:WaitForChild("PlayerGui")local HttpService=game:GetService("HttpService")local TeleportService=game:GetService("TeleportService")local UserInputService=game:GetService("UserInputService")local RunService=game:GetService("RunService")local ReplicatedStorage=game:GetService("ReplicatedStorage")local iddojogo=109983668079237 local character=LocalPlayer.Character or LocalPlayer.CharacterAdded:Wait()local humanoidRootPart=character:WaitForChild("HumanoidRootPart")local plotsCache,lastPlotScan={},0 local PLOT_CACHE_TIME=2 local pathAttempts={{"Base","Spawn","Attachment","AnimalOverhead"},{"Spawn","Attachment","AnimalOverhead"},{"Attachment","AnimalOverhead"},{"AnimalOverhead"}} local netService pcall(function() netService=require(ReplicatedStorage:WaitForChild("Packages").Net) end) local autoInProgress,loopTeleportConnection,prematureDeathConnection=false,nil,nil local targetPosition,isLoopTeleporting=nil,false local function parseGeneration(generationText)if not generationText then return 0 end local textStr=tostring(generationText) local multipliers={B=1e9,b=1e9,M=1e6,m=1e6,K=1e3,k=1e3} local timerPattern1,timerPattern2,numberPattern="%d+m%s*%d+s","%d+:%d+","%d+%.?%d*" if textStr:find(timerPattern1)or textStr:find(timerPattern2)then return 0 end local cleanText=textStr:gsub("[$,/s ]","") local number=tonumber(cleanText:match(numberPattern)) if not number then return 0 end for suffix,mult in pairs(multipliers)do if textStr:find(suffix)then return number*mult end end return number end local function getTextFromObject(obj)if not obj then return nil end local success,value=pcall(function() return obj.Text end) if success and value then return tostring(value) end success,value=pcall(function() return obj.Value end) if success and value then return tostring(value) end return tostring(obj) end local function findPlotsInWorkspace() local currentTime=tick() if currentTime-la