 animalData or nil end local function fastScan() local plots=findPlotsInWorkspace() if #plots==0 then return nil end local highestGeneration=0 local highestAnimal=nil for _,plot in pairs(plots)do for podium=1,23 do local animal=scanSinglePodium(plot,podium) if animal and animal.parsedValue>highestGeneration then highestGeneration=animal.parsedValue highestAnimal=animal end end end return highestAnimal end local function updateStatusLabel(text) if statusLabel and statusLabel.Parent then statusLabel.Text=tostring(text) end end local function doLoopTeleport(position) if not position or not character then return end targetPosition=Vector3.new(position.X,position.Y+5,position.Z) isLoopTeleporting=true if loopTeleportConnection then loopTeleportConnection:Disconnect() end loopTeleportConnection=RunService.Heartbeat:Connect(function() if not isLoopTeleporting or not character then return end local hrp=character:FindFirstChild("HumanoidRootPart") if hrp then hrp.CFrame=CFrame.new(targetPosition) end end) updateStatusLabel("LOCKED TO POSITION!") end local function stopLoopTeleport() isLoopTeleporting=false targetPosition=nil if loopTeleportConnection then loopTeleportConnection:Disconnect() loopTeleportConnection=nil end updateStatusLabel("Loop TP stopped") end local function autoEquipQuantumCloner() if character then local tool=character:FindFirstChildOfClass("Tool") if tool then local n=tool.Name:lower() if n:find("quantum")or n:find("clone")then return true end end end local backpack=LocalPlayer:FindFirstChild("Backpack") if backpack then for _,tool in pairs(backpack:GetChildren())do if tool:IsA("Tool")then local n=tool.Name:lower() if n:find("quantum")or n:find("clone")then if character and character:FindFirstChildOfClass("Humanoid")then character.Humanoid:EquipTool(tool) return true end end end end end return false end local function instantClone() spawn(function() if netService then pcall(function() netService:RemoteEvent("UseItem"):FireServer() end) end if character t